import React, { Component } from "react";
import "./home.component.css";
import UserService from "../services/user.service";
import  Background from "../pics/background.jpeg";
import { Link } from "react-router-dom";

class Home extends Component {
  constructor(props) {
    super(props);

    this.state = {
      content: ""
    };
  }

  componentDidMount() {
    UserService.getPublicContent().then(
      response => {
        this.setState({
          content: response.data
          
        });
        console.log(response.data);
      },
      error => {
        this.setState({
          content:
            (error.response && error.response.data) ||
            error.message ||
            error.toString()
        });
      }
    );
  }

  render() {
    const myStyle={
      backgroundColor: "DodgerBlue",
      /* height:'100vh',
      marginTop:'-70px',
      fontSize:'50px',
      backgroundSize: 'cover',
      backgroundRepeat: 'no-repeat', */
  };

    return (
      <div className="landingbase" >
      {/* style={{backgroundImage: {Background}}}> */}
        {/* <header className="jumbotron">
        <br/>
            <h2>Hiiii Barnali,{this.state.content}</h2>
        </header> */}
      <h1 Style="margin-left: 0%">About Us</h1>
      <div className="testmrquee">
      <marquee width="70%" direction="left" height="100px">
      We travel not to escape life,but for life,not to escape us
        </marquee>
      </div>
        <div className="Container">
            <div className="Upper">
                <div className="MissionStatement">
                    <h2 Style="bold">Why us?</h2>
                    <p>
                        <li>Get the best-in-class bus travel experience </li>
                        <li>We carefully curate the best rated buses .</li>
                        <li>Our journey is Safe, punctual & Comfortable.</li>
                    </p>
                        <h3>Hurry up!! and book your ride now.</h3>
                </div>
                <div className="Values">
                <h2>Our values</h2>
                 <p>
                    <li>We work to be transparent</li>
                    <li>We believe in Autonomy & Accountability</li>
                    <li>We lean towards long-term impact</li>
                    <li>We believe our best perk is amazing peers</li>
                 </p>
            </div>
            </div>
            <div className="Middle">
                <div className="History">
                    <h2>History</h2>
                    <p>
                        <li>Established in 2022</li>
                        <li>Head-office:Kolkata</li>
                        <li>Address:201A,Ripon street</li>
                        <li>Pin:kolkata-700118</li>
                    </p>
                </div>
            </div>
            <div className="Lower">
                <div className="contact">
                    <h2>Reach us here</h2><br></br>
                    <button className="buttndes"><Link to="/Contact">Click Me!</Link></button>
                </div>
            </div>

        </div>

        <br></br>
        <br></br>
        <center><Link className="btn btn-dark btn-lg btnblink" data-toggle="button" to={"/home"}>Get started</Link></center>
      </div>
    );
  }
}
export default Home;
