import React from "react";
import { useState,useEffect } from "react";
import { useLocation } from "react-router-dom";
import service from "../service";
const AddCoupon = () =>
{
    const getuserpswd = useLocation()
    const [company_id,setCompany_id] = useState(0);
    const company_name = getuserpswd.state.username;
    const [location,setlocation] = useState("");
    const [company_email,setCompany_email] = useState("");
    const company_pswd = getuserpswd.state.password;
    const [product_category,setProduct_category] = useState("");
    const [coupon_Id,setCoupon_Id] = useState(0);
    const [coupon_discount,setCoupon_discount] = useState("");
    const [coupon_code,setCoupon_code] = useState("");
    const [product_id,setProduct_id] = useState(0);
    const [coupon_product_name,setCoupon_product_name] = useState("");
    const[product_price,setProduct_price] = useState(0);
    const[coupon_set_name,setCoupon_set_name] = useState("");

    const coupon = {coupon_Id,coupon_discount,coupon_code,product_id,coupon_product_name,product_price,coupon_set_name}
    const company = {company_id,company_name,location,company_email,company_pswd,product_category,coupon}
    
    const addcouponfront = () =>
    {
        service.addnewcoupon(company).then((response) =>{
            console.log(response.data);
        }).catch(console.error);
    }

    useEffect(() => {
        addcouponfront()
      }, []);

       return (
        <div>
            <form>
            <label>
                    company_id: 
                </label>
                <input type="number" required onChange={e=>setCompany_id(e.target.value)}></input>
                <label>
                    location:
                </label>
                <input type="text" required onChange={e=>setlocation(e.target.value)}></input>
                <label>
                    location:
                </label>
                <input type="text" required onChange={e=>setCompany_email(e.target.value)}></input>
                <label>
                    location:
                </label>
                <input type="text" required onChange={e=>setProduct_category(e.target.value)}></input>
                <label>
                    location:
                </label>
                <input type="number" required onChange={e=>setCoupon_Id(e.target.value)}></input>
                <label>
                    location:
                </label>
                <input type="text" required onChange={e=>setlocation(e.target.value)}></input>
                <label>
                    location:
                </label>
                <input type="text" required onChange={e=>setlocation(e.target.value)}></input>
           <button onClick={addcouponfront}>Add Coupon </button>
           </form>
        </div>
       )
} 
export default AddCoupon;
