import React, { Component } from "react";
import service from "../service";
import "./board-company.css";
import authService from "../services/auth.service";
import UserService from "../services/user.service";
import EventBus from "../common/EventBus";
import { useEffect } from "react";
import { useState } from "react";
import { Navigate, useLocation, useNavigate, useParams } from "react-router-dom";

function BoardCompany()
{
const {companyname} = useParams();
const navigatlanding = useNavigate();
 const [allcompany,setAllcompany] = useState([]);
 const fecthdata = () =>
 {
     service.getCupon()
     .then((responsecomp) => {
         setAllcompany(responsecomp.data)
     }).catch((error) => {
         console.log(error)
     })
 }

 const addcoupon = () =>
 { 
    navigatlanding("/addcoupon").then(window.alert("Added successfully")).catch(
      (error)=>console.log(error)
    )
}

const deleteCouponById = (company_id) =>
{
 /*  service.deleteCoupon(company_id).then((reponse)=>console.log("deleted successfullly")
  ).catch((error) => {
    console.log(error)
  }) */
  console
}
 useEffect(() => {
  fecthdata();
  console.log(allcompany);
}, []);
return(
  <div className="landingbase">
  <br></br>
  <br></br>
  <button onClick={addcoupon}>AddCoupon</button>
  <br></br>
  <center><table className="tabledesign" border={3}>

  <tbody>
{
    allcompany.map((company,index)=>(
      <tr>
      <td>{company.company_id}</td>
      <td>{company.company_name}</td>
      <td>{company.location}</td>
      <td>{company.product_category}</td>
      <td>{company.coupon.coupon_code}</td>
      <td>{company.coupon.coupon_product_name}</td>
      <td>{company.coupon.product_price}</td>
      <td>{company.coupon.coupon_discount}%</td>
      <td>{company.coupon.coupon_code}</td>
     {/*  <td><button type="submit" className="btn btn-outline-danger" onClick={()=>deleteCouponById(company.company_id)}>delete</button></td>     */}
      </tr>
    ))
  }
  </tbody>
  </table>
  </center> 
    
  </div>
)
}
export default BoardCompany;
