import React, { Component } from "react";
import service from "../service";

import authService from "../services/auth.service";
import UserService from "../services/user.service";
import EventBus from "../common/EventBus";
import { useEffect } from "react";
import { useState } from "react";
import { Navigate, useLocation, useNavigate, useParams } from "react-router-dom";

/* export default class BoardCompany extends Component {
  constructor(props) {
    super(props);

    this.state = {
      content: ""
    };
  }

  componentDidMount() {
    UserService.getModeratorBoard().then(
      response => {
        this.setState({
          content: response.data
        });
      },
      error => {
        this.setState({
          content:
            (error.response &&
              error.response.data &&
              error.response.data.message) ||
            error.message ||
            error.toString()
        });

        if (error.response && error.response.status === 401) {
          EventBus.dispatch("logout");
        }
      }
    );
  }

  render() {
    return (
      <div className="container">
        <header className="jumbotron">
          <h3>{this.state.content}</h3>
        </header>
      </div>
    );
  }
}
 */
function BoardCompany()
{
const {companyname} = useParams();
const navigatlanding = useNavigate();
 const [allcompany,setAllcompany] = useState([]);
 const fecthdata = () =>
 {
     service.getCupon()
     .then((responsecomp) => {
         setAllcompany(responsecomp.data)
     }).catch((error) => {
         console.log(error)
     })
 }

 const addcoupon = () =>
 {
    
    navigatlanding("/addcoupon")

}

const deleteCoupon = (company_id) =>
{
  service.deleteCoupon(company_id).then(
  window.alert("deleted successfully")
      );
}

 useEffect(() => {
  fecthdata();
  console.log(allcompany);
}, []);
return(
  <div>
  <br></br>
  <br></br>
  <button onClick={addcoupon}>AddCoupon</button>
  <br></br>
  <center><table border="3">

  <tbody>
{
    allcompany.map((company,index)=>(
      <tr>
      <td>{company.company_id}</td>
      <td>{company.company_name}</td>
      <td>{company.location}</td>
      <td>{company.product_category}</td>
      <td>{company.coupon.coupon_code}</td>
      <td>{company.coupon.coupon_product_name}</td>
      <td>{company.coupon.product_price}</td>
      <td>{company.coupon.coupon_discount}%</td>
      <td>{company.coupon.coupon_code}</td>
      <td><button className="btn btn-outline-primary" onClick={deleteCoupon(company.company_id)}>delete</button></td>
      </tr>
    ))
  }
  </tbody>
  </table>
  </center> 
    
  </div>
)


}
export default BoardCompany;
