import React, { Component } from "react";
import service from "../service";
import "./board-company.css";
import authService from "../services/auth.service";
import UserService from "../services/user.service";
import EventBus from "../common/EventBus";
import { useEffect } from "react";
import { useState } from "react";
import { Navigate, useLocation, useNavigate, useParams } from "react-router-dom";

function BoardCompany()
{
const {companyname} = useParams();
const navigatlanding = useNavigate();
 const [allcompany,setAllcompany] = useState([]);
 var sizee=0;
 const fecthdata = () =>
 {
     service.getCupon()
     .then((responsecomp) => {
         setAllcompany(responsecomp.data)
         sizee = responsecomp.data.length
     }).catch((error) => {
         console.log(error)
     })
 }

 const updateCoupon = () =>
 {
    navigatela
 }
 const addcoupon = () =>
 { 
    navigatlanding("/addcoupon").then(()=>{window.alert("Added successfully");sizee++}).catch(
      (error)=>console.log(error)
    )
}

const deleteCouponById = (comp_uid) =>
{
 service.deleteCoupon(comp_uid).then((reponse)=>{console.log("deleted successfullly");sizee--}
  ).catch((error) => {
    console.log(error)
  })
  console.log(comp_uid,"why is automatically called?????????")
}
 useEffect(() => {
  fecthdata();
  console.log(allcompany);
}, [sizee]);
return(
  <div className="landingbase">
  <br></br>
  <br></br>
  <button onClick={addcoupon}>AddCoupon</button>
  <br></br>
  <center><table className="tabledesign" border={3}>

  <tbody>
{
    allcompany.map((company,index)=>(
      <tr>
      <td>{company.company_id}</td>
      <td>{company.company_name}</td>
      <td>{company.location}</td>
      <td>{company.product_category}</td>
      <td>{company.coupon.coupon_code}</td>
      <td>{company.coupon.coupon_product_name}</td>
      <td>{company.coupon.product_price}</td>
      <td>{company.coupon.coupon_discount}%</td>
     <td><button type="button" className="btn btn-danger" onClick={()=>deleteCouponById(company.company_id)}>delete</button></td>
     <td><button type="button" className="btn btn-danger">update</button></td>
      </tr>
    ))
  }
  </tbody>
  </table>
  </center> 
    
  </div>
)
}
export default BoardCompany;
