import React from "react";
import { useState,useEffect } from "react";
import { useLocation } from "react-router-dom";
import service from "../service";
const AddCoupon = () =>
{
    const getuserpswd = useLocation()
    const [company_id,setCompany_id] = useState(122);
    const [company_name,setCompany_name] = useState("Amz3");
    const [location,setlocation] = useState("Mumbai");
    const [company_email,setCompany_email] = useState("amz23@gmail.com");
    const company_pswd = getuserpswd()
    const [product_category,setProduct_category] = useState("electronics");
    const [coupon_Id,setCoupon_Id] = useState(569);
    const [coupon_discount,setCoupon_discount] = useState("65%");
    const [coupon_code,setCoupon_code] = useState("Amz3569");
    const [product_id,setProduct_id] = useState(69877);
    const [coupon_product_name,setCoupon_product_name] = useState("Laptop");
    const[product_price,setProduct_price] = useState(56000);
    const[coupon_set_name,setCoupon_set_name] = useState("Monsoon Sale");

    const coupon = {coupon_Id,coupon_discount,coupon_code,product_id,coupon_product_name,product_price,coupon_set_name}
    const company = {company_id,company_name,location,company_email,company_pswd,product_category,coupon}
    
    const addcouponfront = () =>
    {
        service.addnewcoupon(company).then((response) =>{
            console.log(response.data);
        }).catch(console.error);
    }

    useEffect(() => {
        addcouponfront()
      }, []);

    //    return (
    //     <div>
    //        <button onClick={addcouponfront}>Add Coupon </button>
    //     </div>
    //    )
} 
export default AddCoupon;
